/* eslint-disable jsx-a11y/media-has-caption */
function AssetCard(
  { _id, assetName, fileUrl, fileType, fileSize, duration }: MediaDataType,
  index: number,
  isAssetPage: boolean,
  fetchInitialPayload: any,
) {
  return (
    <List.Item>
      <Card
        title={
          /* eslint-disable-next-line react/jsx-wrap-multilines */
          <div className="flex justify-between items-cemter">
            <Typography.Text className="w-full">{`${index + 1}. ${assetName} `}</Typography.Text>
            {!!isAssetPage && (
              <Popconfirm
                title="Are you sure?"
                onConfirm={() => message.error("This is a demo, you aren't allowed to make changes")}
              >
                <Button type="primary" danger className="absolute right-0 mr-3">
                  <DeleteOutlined style={{ color: '#ffffff' }} />
                </Button>
              </Popconfirm>
            )}
          </div>
        }
      >
        <div className="flex flex-col items-center justify-center h-full space-y-2">
          {fileType.indexOf('image/jpeg') === 0 ? (
            <>
              <Image
                className="max-h-full max-w-full"
                src={fileUrl}
                fallback="data:image/png;base64,"
              />
              <Typography.Text>Duration: {duration}s</Typography.Text>
            </>
          ) : (
            <video className="max-h-full max-w-full" controls>
              <source src={fileUrl} type="video/mp4" />
            </video>
          )}
        </div>
      </Card>
    </List.Item>
  );
}

type MediaDataProps = {
  videoData: MediaDataType[];
  isAssetPage: boolean;
};

