import { Card, List, Image, Button, Badge, Typography } from 'antd';
import { type } from 'os';
import React, { useEffect, useState } from 'react';
import { useUser } from '@auth0/nextjs-auth0';
import gqlInit from './gql';

type VideoDataType = {
  _id: string;
  assetName: string;
  fileUrl: string;
  fileType: string;
  fileSize: number;
  duration: number;
};

/* eslint-disable jsx-a11y/media-has-caption */

function AssetCard(
  { _id, assetName, fileUrl, fileType, fileSize, duration }: VideoDataType,
  index: number,
  assetIdSelected: string[],
  // eslint-disable-next-line no-shadow
  setAssetIdSelected: (assetIdSelected: string[]) => void,
  campaignTotalDuration: number,
  setCampaignTotalDuration: React.Dispatch<React.SetStateAction<number>>,
) {
  const isSelected = assetIdSelected.includes(_id);
  const handlePick = (_id: string, duration: number) => {
    if (!isSelected) {
      setAssetIdSelected([...assetIdSelected, _id]);
      setCampaignTotalDuration(campaignTotalDuration + duration);
    } else {
      setAssetIdSelected(
        assetIdSelected.filter((item) => {
          return item !== _id;
        }),
      );
      setCampaignTotalDuration(campaignTotalDuration - duration);
    }

    // setAssetIdSelected(
    //   !isSelected
    //     ? [...assetIdSelected, _id]
    //     : assetIdSelected.filter((item) => {
    //         return item !== _id;
    //       }),
    // );
  };

  return (
    <List.Item className="hover:cursor-pointer" onClick={(e) => handlePick(_id, duration)}>
      <Badge count={assetIdSelected.indexOf(_id) + 1} showZero={false}>
        <Card
          title={`${index + 1}. ${assetName}`}
          className={isSelected ? 'bg-blue-100 rounded-lg outline-blue-300 ring-blue-300 ring-2 ' : 'rounded-lg'}
          style={{ width: '25rem', height: '21rem' }}
        >
          <div className="flex flex-col items-center justify-center h-full space-y-2 ">
            {fileType.indexOf('image/jpeg') === 0 ? (
              <>
                <Image
                  className="max-h-full max-w-full"
                  src={fileUrl}
                  fallback="data:image/png;base64,"
                />
                <Typography.Text>Duration: {duration}s</Typography.Text>
              </>
            ) : (
              <video className="max-h-full max-w-full" style={{ maxWidth: '20rem' }} controls>
                <source src={fileUrl} type="video/mp4" />
              </video>
            )}
          </div>
        </Card>
      </Badge>
    </List.Item>
  );
}

type VideoDataProps = {
  videoData: VideoDataType[];
  assetIdSelected: string[];
  setAssetIdSelected: (assetIdSelected: string[]) => void;
  campaignTotalDuration: number;
  setCampaignTotalDuration: React.Dispatch<React.SetStateAction<number>>;
};

// eslint-disable-next-line react/function-component-definition
const MediaPick: React.FC<VideoDataProps> = ({
  videoData,
  assetIdSelected,
  setAssetIdSelected,
  campaignTotalDuration,
  setCampaignTotalDuration,
}) => {
  // hooks

  return (
    <div>
      <List
        grid={{
          gutter: 30,
          xs: 1,
          sm: 1,
          md: 2,
          lg: 2,
          xl: 3,
          xxl: 4,
        }}
        dataSource={videoData}
        renderItem={
          (item, index) =>
            // eslint-disable-next-line implicit-arrow-linebreak
            AssetCard(item, index, assetIdSelected, setAssetIdSelected, campaignTotalDuration, setCampaignTotalDuration)
          // eslint-disable-next-line react/jsx-curly-newline
        }
      />
    </div>
  );
};

export default MediaPick;

